'use strict';

var consts = require('./consts');

/**
 * 获取缓存数据
 * @param {string} key
 * @returns
 *
 * @example
 * getCache('USER_INFO');
 */
function getCache(key) {
  var caches = window[consts.BUTILS_CACHE] || {};

  if (caches.hasOwnProperty(key)) {
    return caches[key];
  }

  return null;
}

module.exports = getCache;
