'use strict';

/**
 * 判断元素是否有某个class类名
 * @param element HTML元素
 * @param className 类名
 *
 * @example
 * hasClass(someEl, 'hide')
 */
function hasClass(element, className) {
  if (!element || !element.classList) return false;
  return element.classList.contains(className);
}

module.exports = hasClass;
