'use strict';

var consts = require('./consts');

/**
 * 判断一个变量是不是身份证号码
 * @param {*} value
 *
 * @example
 * isIdCard('632123820927051');    // => true
 * isIdCard('22082119800801002x'); // => true
 */
function isIdCard(value) {
  return consts.RE_ID_CARD.test(value);
}

module.exports = isIdCard;
