'use strict';

/**
 * 判断是否为润年
 * @param {number} year
 * @returns {boolean}
 *
 * 1. 直接判断年份
 * isLeapYear(2012); // => true
 * isLeapYear(2013); // => false
 *
 * 2. 传入一个日期
 * isLeapYear(new Date());
 */
function isLeapYear(year) {
  return year % 4 === 0 && (year % 100 !== 0 || year % 400 === 0);
}

module.exports = isLeapYear;
